import React, { Component } from 'react'
import InfiniteScroll from "react-infinite-scroll-component";

import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
export class News extends Component {
  static defaultProps =
  {
    country: 'in',
    catagory: 'general',
    pageSize: 20,
  }
  static propType =
    {
      country: PropTypes.string,
      catagory: PropTypes.string,
      pageSize: PropTypes.number
    }
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      page: 1,
      loading: false,
      totalResults:0

    }
  }
  async updateState() {
    let api = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=98ebd886b4cb4c75925b11848c4adc30&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true })
    let data = await fetch(api);
    let parseData = await data.json();
    this.setState({
      articles: parseData.articles,
      totalResults: parseData.totalResults,
      loading: false,
  })}
  fetchMoreData = async()=>
  {
    let api = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=98ebd886b4cb4c75925b11848c4adc30&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true })
    let data = await fetch(api);
    let parseData = await data.json();
    this.setState({
      articles: this.state.articles.concat(parseData.articles),
      totalResults: parseData.totalResults,
      loading: false,

    })
  }
  async componentDidMount() {
    this.updateState();
  }
 
  render() {
    return (

      <div className='container my-3' >
        {this.state.loading && <Spinner />}
        <h1 className='container my-3 text-center' style={{ fontWeight: 'bolder' }}> Hindustan Khabar - Top headlines from {this.props.category}</h1>
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length!==this.state.totalResults}
          loader={<Spinner/>}>
        <div className='row' >
          
          {this.state.articles.map((elements) => {
            
            return <div className='col-md-3 ' key={elements.url}>
              <NewsItem title={elements.title?elements.title.slice(0, 45):"N/A"} source={elements.source.name?elements.source.name:"N/A"} author={elements.author?elements.author:"N/A"} date={elements.publishedAt?elements.publishedAt:"N/A"} description={elements.description?elements.description.slice(0, 88):"N/A"} img={elements.urlToImage?elements.urlToImage:"N/A"} url={elements.url} />
            </div>
          })}
        </div>
          </InfiniteScroll>
       
      </div>
    )
  }
}

export default News
