import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: []
    }
  }
  async componentDidMount() {
    let api = "https://newsapi.org/v2/everything?q=Apple&from=2022-03-24&sortBy=popularity&apiKey=98ebd886b4cb4c75925b11848c4adc30";
    let data = await fetch(api);
    let parseData = await data.json();
    this.setState({ articles: parseData.articles })
  }
  render() {

    return (
      <div className='container my-3' >
        <h1 className='container my-3' >Hindustan Khabar - Top headlines</h1>
        <div className='row' >
          {this.state.articles.map((elements) => {
            return <div className='col-md-4 ' key={elements.url}>
              <NewsItem title={elements.title.slice(0, 45)} description={elements.description.slice(0, 88)} img={elements.urlToImage} url={elements.url} />
            </div>
          })}
        </div>
        <div className='container d-flex justify-content-around'>
        <button type="button" class="btn btn-dark ">Previous</button>
        <button type="button" class="btn btn-dark">Next</button>
        </div>
      </div>
    )
  }
}

export default News
