import React, {useEffect, useState} from 'react'
import InfiniteScroll from "react-infinite-scroll-component";
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
const News=(props)=>  {
 const [articles, setarticles] = useState([])
 const [page, setpage] = useState(1)
 const [loading, setloading] = useState(true)
 const [totalResults, settotalResults] = useState(0)
  
  const updateState=async()=> {
    props.setProgress(10);
    const api = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`;
    props.setProgress(15)
    setloading(true );
    props.setProgress(35);
    let data = await fetch(api);
    let parseData = await data.json();
    props.setProgress(75);
    setarticles(parseData.articles)
    settotalResults(parseData.totalResults)
    setloading(false)
    props.setProgress(100);
  }
  useEffect(() => {
  updateNews();}
  , [])
  
  const fetchMoreData = async () => {
    setpage(page + 1 )
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=${props.apiKey}&page=${page}&pageSize=${props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({
      articles: this.state.articles.concat(parsedData.articles),
      totalResults: parsedData.totalResults,

    })
  };


    return (
      <>

        <h1 className='container my-3 text-center' style={{ fontWeight: 'bolder' }}> Hindustan Khabar - Top headlines from {props.category}</h1>
        {loading && <Spinner />}
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResults}
          loader={<Spinner />}>
          <div className='container'>
            <div className='row' >

              {this.state.articles.map((elements) => {

                return <div className='col-md-4 ' key={elements.url}>    
                                <NewsItem title={elements.title ? elements.title: ""} description={elements.description ? elements.description.slice(0,55) : "N/A"} imageUrl={elements.urlToImage?elements.urlToImage:"https://thumbs.dreamstime.com/z/website-maintenance-notice-toy-construction-figures-excavator-isolated-white-background-46860761.jpg"} newsUrl={elements.url?elements.url:"N/A"} author={elements.author?elements.author:"N/A"} date={elements.publishedAt?elements.publishedAt:"N/A"} source={elements.source.name?elements.source.name:"N/A"} />
                </div>
              })}
            </div>
          </div>
        </InfiniteScroll>
      </>


    )
  }


News.propTypes =
{
  country: PropTypes.string,
  catagory: PropTypes.string,
  pageSize: PropTypes.number,
 
}
export default News
