import InfiniteScroll from 'react-infinite-scroll-component';
import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
export class News extends Component {
  static propType =
    {
      country: PropTypes.string,
      catagory: PropTypes.string,
      pageSize: PropTypes.number
    }
  constructor() {
    super();

    this.state = {
      articles: [],
      page: 1,
      loading: false
    }
  }
  async updateState() {
    let api = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=98ebd886b4cb4c75925b11848c4adc30&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
    this.setState({ loading: true })
    let data = await fetch(api);
    let parseData = await data.json();
    this.setState({
      articles: parseData.articles,
      totalResults: parseData.totalResults,
      loading: false,

    })
  }
  async componentDidMount() {
    this.updateState();
  }
  handleNext = async () => {
    this.updateState();
    this.setState({page:this.state.page + 1})
  }
  handlePrevious = async () => {
    this.updateState();
    this.setState({page:this.state.page - 1})

   
  }
  render() {
    return (

      <div className='container my-3' >
        {this.state.loading && <Spinner />}
        <h1 className='container my-3 text-center' style={{ fontWeight: 'bolder' }}> Hindustan Khabar - Top headlines from {this.props.category}</h1>

        <div className='row' >
          {!this.state.loading && this.state.articles.map((elements) => {
            return <div className='col-md-4 ' key={elements.url}>
              <NewsItem title={elements.title?elements.title.slice(0, 45):"N/A"} source={elements.source.name?elements.source.name:"N/A"} author={elements.author?elements.author:"N/A"} date={elements.publishedAt?elements.publishedAt:"N/A"} description={elements.description.slice(0, 88)} img={elements.urlToImage} url={elements.url} />
            </div>
          })}
        </div>
        <div className='container d-flex justify-content-around'>
          <button type="button" disabled={this.state.page <= 1} className="btn btn-dark " onClick={this.handlePrevious}>&#x2B05; Previous</button>
          <button type="button" className="btn btn-dark" onClick={this.handleNext}>Next 	&#x27A1;</button>
        </div>
      </div>
    )
  }
}

export default News
