import React, { useEffect,useState } from 'react'
import InfiniteScroll from "react-infinite-scroll-component";
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
const News=(props)=> {
 const [Articles, setArticles] = useState([])
 const [page, setpage] = useState(1)
 const [loading, setloading] = useState(true)
 const [totalResults, settotalResults] = useState(0)
 
  const updateNews=async()=> {
    props.setProgress(7);
    const api = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=8915975fe8244e9eba7ef3b6f80bebcc&page=${page}&pageSize=${props.pageSize}`;
   setloading(true)
    
    let data = await fetch(api);
    props.setProgress(55);
    let parseData = await data.json();
    props.setProgress(70);
    setArticles(parseData.Articles)
        settotalResults(parseData.totalResults)
        setloading(false)
        props.setProgress(100);
   
  }
 useEffect(() => {
  updateNews();
 }, [])
 
  const fetchMoreData = async () => {
    const url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apikey=8915975fe8244e9eba7ef3b6f80bebcc&page=${page}&pageSize=${props.pageSize}`;
    setpage(page+1)
    let data = await fetch(url);
    let parsedData = await data.json()
    setArticles(Articles.concat(parsedData.Articles))
    settotalResults(parsedData.totalResults)
    
  };


  return (
    <>
        <h1 className="text-center" style={{ margin: '35px 0px', marginTop: '90px' }}>NewsMonkey - Top {(props.category)} Headlines</h1>
        {loading && <Spinner />}
        <InfiniteScroll
            dataLength={Articles.length}
            next={fetchMoreData}
            hasMore={Articles.length !== totalResults}
            loader={<Spinner/>}
        > 
            <div className="container">
                 
            <div className="row">
                {Articles.map((elements) => {
                    return <div className="col-md-4" key={elements.url}>
                        <NewsItem title={elements.title ? elements.title : ""} description={elements.description ? elements.description : ""} imageUrl={elements.urlToImage} newsUrl={elements.url} author={elements.author} date={elements.publishedAt} source={elements.source.name} />
                    </div>
                })}
            </div>
            </div> 
        </InfiniteScroll>
    </>

    )
  }

News.defaultProps =
{
  country: 'in',
  catagory: 'general', 
  pageSize: 8,
  
}
News.propTypes =
{
  country: PropTypes.string,
  catagory: PropTypes.string,
  pageSize: PropTypes.number,
 
}
export default News
