import React, { Component } from 'react'
import InfiniteScroll from "react-infinite-scroll-component";
import NewsItem from './NewsItem'
import Spinner from './Spinner';
import PropTypes from 'prop-types'
export class News extends Component {
 
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      page: 1,
      loading: true,
      totalResults: 0

    }
  }
  async updateState() {
    this.props.setProgress(10);
    const api = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.apiKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.props.setProgress(15)
    this.setState({ loading: true });
    this.props.setProgress(35);
    let data = await fetch(api);
    this.props.setProgress(55);
    let parseData = await data.json();
    this.props.setProgress(75);
    this.setState({
      articles: parseData.articles,
      totalResults: parseData.totalResults,
      loading: false,
    })
    this.props.setProgress(100);
  }
  async componentDidMount() {
    this.updateState();
  }
  fetchMoreData = async () => {
    this.setState({ page: this.state.page + 1 })
    const url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apikey=${this.props.apiKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json()
    this.setState({
      articles: this.state.articles.concat(parsedData.articles),
      totalResults: parsedData.totalResults,

    })
  };


  render() {
    return (
      <>

        <h1 className='container my-3 text-center' style={{ fontWeight: 'bolder' }}> Hindustan Khabar - Top headlines from {this.props.category}</h1>
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !== this.state.totalResults}
          loader={<Spinner />}>
          <div className='container'>
            <div className='row' >

              {this.state.articles.map((elements) => {

                return <div className='col-md-4 ' key={elements.url}>    
                                <NewsItem title={elements.title ? elements.title: ""} description={elements.description ? elements.description.slice(0,55) : "N/A"} imageUrl={elements.urlToImage?elements.urlToImage:"https://thumbs.dreamstime.com/z/website-maintenance-notice-toy-construction-figures-excavator-isolated-white-background-46860761.jpg"} newsUrl={elements.url?elements.url:"N/A"} author={elements.author?elements.author:"N/A"} date={elements.publishedAt?elements.publishedAt:"N/A"} source={elements.source.name?elements.source.name:"N/A"} />
                </div>
              })}
            </div>
          </div>
        </InfiniteScroll>
      </>


    )
  }
}
News.defaultProps =
{
  country: 'in',
  catagory: 'general', 
  pageSize: 8,
  
}
News.propTypes =
{
  country: PropTypes.string,
  catagory: PropTypes.string,
  pageSize: PropTypes.number,
 
}
export default News
